<?php
class DB{private static ?PDO $pdo=null;public static function pdo():PDO{if(self::$pdo===null){$cfg=require __DIR__.'/../config/env.php';$dsn="mysql:host={$cfg['db_host']};dbname={$cfg['db_name']};charset={$cfg['db_charset']}";self::$pdo=new PDO($dsn,$cfg['db_user'],$cfg['db_pass'],[PDO::ATTR_ERRMODE=>PDO::ERRMODE_EXCEPTION,PDO::ATTR_DEFAULT_FETCH_MODE=>PDO::FETCH_ASSOC,]);}return self::$pdo;}}
