<!doctype html><html><head><meta charset='utf-8'><title>Productos</title><link rel='stylesheet' href='assets/styles.css'></head><body><h2>Productos</h2><form method='post' action='index.php?r=product/save'><label>Nombre <input name='nombre' required></label> <label>Precio <input name='precio' type='number' step='0.01' required></label> <label>Stock <input name='stock' type='number' required></label> <label>Categoría <input name='categoria'></label> <button class='btn'>Agregar</button></form><?php if(!empty($error)):?><p class='err'><?=h($error)?></p><?php endif;?><table><tr><th>ID</th><th>Nombre</th><th>Precio</th><th>Stock</th><th>Categoría</th><th>Acciones</th></tr><?php foreach($rows as $r):?><tr><td><?=h($r['idproducto'])?></td><td><?=h($r['nombre'])?></td><td><?=h($r['precio'])?></td><td><?=h($r['stock'])?></td><td><?=h($r['categoria'])?></td><td><a class='btn' href='index.php?r=product/delete&id=<?=$r['idproducto']?>'>Eliminar</a></td></tr><?php endforeach;?></table><p><a class='btn' href='index.php?r=home'>← Volver</a></p></body></html>